public class IapHelper {

	/**
	 * 请求支付数据
	 * @param receipt
	 * @return
	 * @throws IOException 
	 * @throws HttpException 
	 */
	public static String getPayData(String receipt) throws HttpException, IOException{
		return getPayData(receipt, IapConfig.VERIFY_URL);
	}
	
	public static String getPayData(String receipt, String verifyUrl) throws HttpException, IOException{
		Map<String,String> content = new HashMap<>();
		content.put(IapConfig.REQUEST_CONTENT_KEY, receipt);
		content.put("password", IapConfig.REQUEST_CONTENT_PASSWORD);
		ResponseStatus response = HttpUtils.postJsonEntity(verifyUrl, JsonHelper.toJson(content));
		return response.getContent();
	}
	
	public static void main(String[] args) throws HttpException, IOException {
		System.err.println(Tools.getDateTime(1502848322492L));
		System.err.println(Tools.getDateTime("2016-04-28 10:18:49 Etc/GMT"));
		String rec = ""+
				"";
		System.err.println(getPayData(rec));
	}
	
}
